}
    
      
     }
  
      
    
  };
     /*
check (type)
    /**
   * onChangetype submission 
   * @function onChangetype
   * @param  {event} - submit event
   */
  onChangetype = e =>{
  this.setState({
    type:e.target.value
  })
  }
     /*
check (email)
      /**
   * onChangeemail submission 
   * @function onChangeemail
   * @param  {event} - submit event
   */

  onChangeemail = e => {
    this.setState({
      email: e.target.value
    });
    let emailerror = this.state.emailError;
    emailerror =
      !emailRegax.test(e.target.value) ||
      e.target.value.trim() === "" ||
      e.target.value.length > 100 ||
      e.target.value.length < 5 ||
      e.target.value.length === 0
        ? "InvalidEmail!"
        : "";
    this.setState({ emailError: emailerror });
  };

     /*
check (username)
   /**
   * onChangeusername submission 
   * @function onChangeusername
   * @param  {event} - submit event
   */
  onChangeusername = e => {
    this.setState({
      userName: e.target.value
    });
    let usernameerror = this.state.usernameError;
    usernameerror =
      e.target.value.length > 16 ||
      e.target.value.length === 0 ||
      e.target.value.trim() === "" ||
      e.target.value.length < 6
        ? "minimum 6charchters And Maxmium 16 charchters are required"
        : "";
    this.setState({ usernameError: usernameerror });
  };
       /*
check (Password length by >8 and less than 16)
    /**
   * onChangepassword submission 
   * @function onChangepassword
   * @param  {event} - submit event
   */
