    this.setState({
      password: e.target.value
    });
    let passworderror = this.state.passwordError;
    passworderror =
      e.target.value.length > 16 ||
      e.target.value.trim() === "" ||
      e.target.value.length === 0 ||
      e.target.value.length < 8
        ? "minimum 8 charchters And Maxmium 16 charchters are required to make strong password"
        : "";
    //this.state.passwordError=passworderror;
    this.setState({ passwordError: passworderror });
  };
       /*
check (age must be >10 )
    /**
   * onchangedateday submission 
   * @function onchangedateday
   * @param  {event} - submit event
   */
  onchangedateday = e => {
    if(e.target.value<=9 || e.target.value >=90)
    {
      this.setState({ageerror:" Sorry!! Min age is 10 an Max age is 90"})
    }
    else{
    this.setState({
      age: e.target.value,
      ageerror:""
    });
  }
}
     /*
check (gender)
     /**
   * onChangegender 
   * @function onChangegender
   * @param  {event} - submit event
   */

  onChangegender = e => {
    this.setState({
      gender: e.target.value
    });
  }
       /*
check (product)
     /**
   * onChangeproduct 
   * @function onChangeproduct
   * @param  {event} - submit event
   */

  onChangeproduct = e=>{
    this.setState({
      product: e.target.value
    });
  }
       /*
check (when user want to go login)
     /**
   * Go to login page 
   * @function gologin
   * @param  {event} - submit event
   */
  gologin = e => {
    return (window.location.href = "/login");
  }
  
  render()
   {

