            onChange={this.onChangeemail}
            type="text"
            placeholder="Email"
            name="email"
            className="forminput"
            id ="emailid"
            required="required"
          />

          <div className="errormsg">{this.state.emailError}</div>
          <br></br>

          <input
            onChange={this.onChangeusername}
            type="text"
            placeholder="UserNamee"
            name="userName"
            className="forminput"
            id="usernameid"
            required="required"
          />

          <div className="errormsg">{this.state.usernameError}</div>
          <br></br>

          <input
            onChange={this.onChangepassword}
            type="password"
            placeholder="Password"
            name="password"
            className="forminput"
            id ="passwordid"
            required="required"
          />

          <div className="errormsg">{this.state.passwordError}</div>
          <br></br>

          
            <input
            onChange={this.onchangedateday}
            type="number"
            placeholder="YourAge"
            name="age"
            className="forminput"
            maxLength="2"
            minLength="1"
            pattern="[0-9]*" 
            id="ageid"
            required="required"
          />
          <div className="errormsg">{this.state.ageerror}</div>
          
          <br></br>

          
         <label>gender:</label>
          <br></br>
          <select
            id="select"
            className="forminput"
            onChange={this.onChangegender}
            name="gender"
            placeholder={this.state.gender}
          >
            <option defaultValue="Female">Female</option>
            <option value="Male">Male</option>
          </select>
 

          <br></br>

<label>type:</label>
<br></br>
          <select id="sss" className="forminput" name="type" onChange={this.onChangetype} required="required" >
            <option defaultValue ="User"> User</option>
            <option value ="Artist">Artist</option>
          </select>

          <br></br>

<label>product:</label>
<br></br>
          <select id="sss" className="forminput" name="product" onChange={this.onChangeproduct} required="required" >
            <option defaultValue ="Premium"> Premium</option>
            <option value ="Free">Free</option>
</select>

          <br></br>
          <br></br>

          <button className="btn" onClick={this.onSubmit}>
            {" "}
            SIGN UP
          </button>
          <h3> Already have account?</h3>
          <button id= "forlogin" onClick={this.gologin}>Login</button>

        </div>

      </form>
    );
    }
    
  
  

  }

export default SignupForm;
