// export default Follow;

class Follow extends Component {
    constructor(props) {
      super(props);
      this.state = {
        switched: false
      };
    }

    toggleSwitch = () => {
      this.setState(prevState => {
        return {
          switched: !prevState.switched
        };
      });
    };

    render() {
      return (
          <div>

              <Switch onClick={this.toggleSwitch} on={this.state.switched}/>


          </div>
      );
    }

  }

  export default Follow;
