import React, { Component } from "react";
import "./login.css";
import "./page.js";
//import axios from 'axios';
//import { Redirect } from 'react-router-dom';
//import {Router} from "react-router-dom";

class Loginform extends Component {
  constructor(props) {
    super(props);
    this.state = {
      identifier: "",
      password: "",
      isLoading: false,
      nameError: "",
      passwordError: "",
      loggedIn: false
    };

    this.onSubmit = this.onSubmit.bind(this);
    this.onChange = this.onChange.bind(this);
  }

  onChange = e => {
    e.preventDefault();
    this.setState({ [e.target.name]: e.target.value });
  };

  isValid() {
    if (this.state.identifier.length < 1 && this.state.password.length < 1) {
      this.setState({
        nameError: "Please enter your Spotify username or email address.",
        passwordError: "Please enter your password."
      });
    } else if (
      this.state.identifier.length < 1 &&
      this.state.password.length > 0
    ) {
      this.setState({
        nameError: "Please enter your Spotify username or email address."
      });
    } else if (
      this.state.password.length < 1 &&
      this.state.identifier.length > 1
    ) {
      this.setState({ passwordError: "Please enter your password." });
    } else {
      return true;
    }
  }

  onSubmit = e => {
    this.setState({ nameError: "", passwordError: "" });
    e.preventDefault();

    if (this.isValid()) {
      //----- This lines are must when integrating with the backend not now
      /* let input = document.querySelectorAll('.center');

       const data = {

           email: input[0].identifier,
           password: input[1].password,
           

       }

   return axios.post('http://localhost:3000/users' , data)
    .then(response => {
    console.log(response)
        if(response.data.token) {
         //Finish Login
        }
        else {
            alert('please check your username or password')
        }
        
    })
   }
   //CatchErrorOfServer
   
}*/

      //-------------------------------------------------------
      //Here's to only fake log in without any connections with database

      // return axios.post('http://localhost:3000/users' , this.state)
      // .then(response => {
      this.setState({
        loggedIn: true
      });
      //     console.log(this.state)
      // })
    }
  };
  render() {
    const { identifier, password, isLoading } = this.state;
    if (this.state.loggedIn) {
      return (window.location.href = "/Home");
    }

    return (
      <form id="loginForm">
        <h3> To continue,log in to Spotify </h3>
        <button
          className="btn2"
          onClick={() => {
            alert("try later");
          }}
          disabled={isLoading}
        >
          CONTINUE WITH FACEBOOK
        </button>
        <h3>OR</h3>
        <input
          name="identifier"
          field="identifier"
          value={identifier}
          className="center"
          type="text"
          placeholder="Enter Username or Email "
          onChange={this.onChange}
        />
        <div className="error">{this.state.nameError}</div> <br />
        <input
          name="password"
          field="password"
          value={password}
          type="password"
          className="center"
          placeholder="Password"
          onChange={this.onChange}
        />
        <div className="error">{this.state.passwordError}</div> <br />
        <button className="btn" onClick={this.onSubmit} disabled={isLoading}>
          LOG IN
        </button>{" "}
        <br />
        <a href="/page" className="hyperLink">
          Forgot your password?
        </a>
        <hr />
        <h3>Don't have an account ?</h3>
        <a className="btn3" href="/signup" disabled={isLoading}>
          SIGN UP FOR SPOTIFY
        </a>
      </form>
    );
  }
}

export default Loginform;
