    }

    handleChange(checked) {
      this.setState({ checked });
    }

    render() {
      return (
        <div >
            <Switch
              onChange={this.handleChange}
              checked={this.state.checked}
              className="react-switch"
            />
           <span>{this.state.checked ? 'Follow' : 'Unfollow'}</span>
        </div>
      );
