import React from "react";
import "./ArtistPage.css";
import Follow from './Follow';
import { post } from "axios";
class ArtistPage extends React.Component {
  constructor() {
    super();
    this.state = { checked: false };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(checked) {
    this.setState({ checked : true  });
  }
  
  state = {
    selectedFile: null,
  };
  fileSelectedHandler = (event) => {
    this.setState({
      selectedFile: event.target.files[0],
    });
  };
  fileUploadHandler = () => {
    const fd = new FormData();
    fd.append("song", this.state.selectedFile, this.state.selectedFile.name);
    return post("/songs.json", fd, {
      onUploadProgress: (ProgressEvent) => {
        console.log(
          "upload progress" +
            Math.round((ProgressEvent.loaded / ProgressEvent.total) * 100) +
            "%"
        );
      },
    }).then((respond) => {
      console.log(respond);
    });
  };

  render() {
    return (
      <div className="ArtistPageLayout">
        <div className="Artist-profile">
          <h1 className="name">Amr Diab</h1>
          <ul className="buttons">
            <li id="play-btn">PLAY</li>
            <Follow ></Follow>
            <li
              className="pickFileButton"
              onClick={() => this.fileInput.click()}
            >
              <div onSubmit={this.onSubmit}>
                <input
                  type="file"
                  name="song"
                  style={{ display: "none" }}
                  onChange={this.fileSelectedHandler}
                  ref={(fileInput) => (this.fileInput = fileInput)}
                />
              </div>
              Pick Up
            </li>
            <li className="upload" onClick={this.fileUploadHandler}>
              Upload
            </li>
          </ul>
          <ul className="icons">
            <li>
              <a href="/amer-mounib/overview" id="overview-icon">
                Overview
              </a>
            </li>
            <li>
              <a href="/amer-mounib/related-artists" id="relate-icon">
                Related Artists
              </a>
            </li>
            <li>
              <a href="/amer-mounib/about" id="about-icon">
                About
              </a>
            </li>
          </ul>
        </div>
        <div></div>
      </div>
    );
  }
}
export default ArtistPage;
